<style>
  .hero {
    height: 100vh;
    width: 100%;
    display: grid;
    place-items: center;
  }
  .text {
    position: absolute;
    top: 70%;
    z-index: 1;
    text-align: center;
  }

  .shadow {
    text-shadow: var(--bg) 0px 0px 10px;
  }
  .wrapper {
    padding-top: 5rem;
    position: relative;
    display: grid;
    place-items: center;
    width: fit-content;
  }

  .circle {
    cursor: pointer;
    position: absolute;
    display: grid;
    place-items: center;
    transform: translate(-50%, -50%);
    z-index: 2;
  }
  img {
    width: 45vw;
    filter: brightness(80%);
    mask: linear-gradient(
      var(--bg),
      var(--bg),
      var(--bg),
      var(--bg),
      var(--bg),
      transparent
    );
  }
  .ring {
    position: absolute;
    bottom: 10%;
    width: 85%;
    fill: #c8cacb;
    animation: rotate infinite 350s linear;
  }

  .circle svg {
    width: 60%;
  }

  @keyframes rotate {
    from {
      transform: rotate(0deg);
    }
    to {
      transform: rotate(-360deg);
    }
  }
</style>
